const LOAD_ALL_PRODUCTS = 'products/LOAD_ALL_PRODUCTS'


const loadAllProducts = list => ({
    type: LOAD_ALL_PRODUCTS,
    list
})


export const getProductsThunk = () => async dispatch => {
    const response = await fetch(`/api/products`)

    if(response.ok) {
        const allProducts = await response.json()
        dispatch(loadAllProducts(allProducts))
        return allProducts
    }
}


const initialState = {
    allProducts: {},
    singleProduct: {}
}


const productsReducer = (state = initialState, action) => {
    switch (action.type) {
        case LOAD_ALL_PRODUCTS: {
            const allProductsState = {}
            action.list.forEach(product => (
                allProductsState[productsReducer.id] = product
            ))
            return {
                ...state,
                allProducts: allProductsState,
                singleProduct: {}
            }
        }

        default:
            return state
    }
}


export default productsReducer
